// ref: https://www.reddit.com/r/olkb/comments/og1q4u/listen_for_every_keypress_with_qmk/
bool g_bongo_state = false;

bool process_record_user(uint16_t keycode, keyrecord_t *record) {
#ifdef CONSOLE_ENABLE
    uprintf("KL: kc: 0x%04X, col: %2u, row: %2u, pressed: %u, time: %5u\n", keycode, record->event.key.col, record->event.key.row, record->event.pressed, record->event.time);
#endif

    if (record->event.pressed) {
#ifdef OLED_ENABLE
        if (g_bongo_state) {
            render_bongo_down();
        } else {
            render_bongo_raise();
        }
        g_bongo_state = !g_bongo_state;
#endif
    }
    return true;
}

